import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'

const MiApi = () => {
    const [apiContent, setApiContent] = useState([]);
    const [paisBuscado, setPaisBuscado] = useState("");
    const [filtroDePaisActivado, setFiltroDePaisActivado] = useState(false);
    const [listaFiltrada, setListaFiltrada] = useState([])

    const firstUrl = 'https://restcountries.com/v2/all';

    const llamarApi = async (endPoint) => {
        const url = endPoint;
        const response = await fetch(url);
        const data = await response.json();
        const orderedData = data.sort((a, b) => a.translations.es.localeCompare(b.translations.es));
        setApiContent(orderedData);
    }

    useEffect(() => {
        llamarApi(firstUrl);
    }, [])

    const capturarPais = (e) => {
        setPaisBuscado(e.target.value);
    }

    const detallesPais = () => {
        setFiltroDePaisActivado(true);
        setListaFiltrada(apiContent.filter(country => country.translations.es === paisBuscado));
        setPaisBuscado("");
    }

    const autocomplete = (e) => {
        console.log("evento keydown");
    }

    return (
        <>
            <div className="row align-items-start container bg-dark text-white p-3 m-auto border rounded">
                <div className="col">
                    <h5 className='mb-3 text-center'>Búsqueda por país</h5>
                    <div className="input-group mb-3">
                        <button className="btn btn-outline-light" onClick={detallesPais} type="button">Buscar</button>
                        <input aria-label='Search for a country'
                            aria-autocomplete='both'
                            aria-controls='autocomplete-results'
                            type="text"
                            className="form-control"
                            onChange={capturarPais}
                            value={paisBuscado}
                            onKeyDown={autocomplete} />
                    </div>
                </div>
                <div className="col">
                    <h5 className='mb-3 text-center'>Más información</h5>
                    <div className="dropdown">
                        <button className="btn btn-outline-light dropdown-toggle container" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                            Filtros disponibles
                        </button>
                        <ul className="dropdown-menu container text-center">
                            <li><a className="dropdown-item" href="#">Ordenar por mayor población</a></li>
                            <li><a className="dropdown-item" href="#">Ordenar por menor población</a></li>
                            <li><a className="dropdown-item" href="#">Ordenar por mayor área</a></li>
                            <li><a className="dropdown-item" href="#">Ordenar por menor área</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de Europa</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de America del Sur</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de America Central</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de America del Norte</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de Africa</a></li>
                            <li><a className="dropdown-item" href="#">Banderas de Asía</a></li>
                        </ul>
                    </div>
                </div>
            </div>

            <div className='row p-5 m-0'>
                {filtroDePaisActivado === false ?
                    apiContent.map((country) => (
                        <div key={country.name} className='col'>
                            <div className='card mb-2 bg-dark text-white' style={{ width: "200px", height: "315px" }}>
                                <img className='p-2' style={{ height: "140px" }} src={country.flag} alt="" />
                                <div className='card-body'>
                                    <h5 className='card-title'>{country.translations.es}</h5>
                                    <p className='card-text mb-0'>Región: {country.region}</p>
                                    <p className='card-text'>Capital: {country.capital}</p>
                                </div>
                            </div>
                        </div>))
                    :
                    listaFiltrada.map(country =>
                        <div className="row align-items-center">
                            <div className="col-6">
                                <img src={country.flag} className="img-fluid" alt="" />
                            </div>
                            <div className="col-4">
                                One of three columns
                            </div>
                        </div>

                    )
                }
            </div>

        </>
    )
}

export default MiApi
